import React, { useState, useEffect, useRef } from 'react';

const RomanticBirthdayPage = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showHearts, setShowHearts] = useState(false);
  const [showKiss, setShowKiss] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    // Auto-play background music when component mounts
    if (audioRef.current) {
      audioRef.current.volume = 0.3;
      audioRef.current.loop = true;
      
      // Attempt to autoplay the music
      const playMusic = async () => {
        try {
          await audioRef.current.play();
          setIsPlaying(true);
        } catch (error) {
          console.log('Autoplay prevented by browser. User needs to interact first.');
          // Set up a click listener to start music on first user interaction
          const startMusicOnInteraction = () => {
            audioRef.current.play().then(() => {
              setIsPlaying(true);
            }).catch(err => console.log('Music start failed:', err));
            document.removeEventListener('click', startMusicOnInteraction);
            document.removeEventListener('touchstart', startMusicOnInteraction);
          };
          document.addEventListener('click', startMusicOnInteraction);
          document.addEventListener('touchstart', startMusicOnInteraction);
        }
      };
      
      playMusic();
    }
  }, []);

  const toggleMusic = () => {
    if (isPlaying) {
      audioRef.current.pause();
      setIsPlaying(false);
    } else {
      audioRef.current.play();
      setIsPlaying(true);
    }
  };

  const handleHug = () => {
    setShowHearts(true);
    setTimeout(() => setShowHearts(false), 3000);
  };

  const handleKiss = () => {
    setShowKiss(true);
    setTimeout(() => setShowKiss(false), 2000);
  };

  const handleLove = () => {
    setShowConfetti(true);
    setTimeout(() => setShowConfetti(false), 4000);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-100 via-red-50 to-pink-200 relative overflow-hidden">
      {/* Background Music */}
      <audio
        ref={audioRef}
        src="https://www.soundjay.com/misc/sounds/bell-ringing-05.wav"
        preload="auto"
      />
      
      {/* Floating Hearts Background */}
      <div className="absolute inset-0 pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute text-pink-300 opacity-30 animate-float"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 3}s`,
              animationDuration: `${3 + Math.random() * 4}s`,
            }}
          >
            ❤️
          </div>
        ))}
      </div>

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
        <div className="max-w-2xl w-full">
          {/* Main Card */}
          <div className="bg-white/90 backdrop-blur-sm rounded-3xl shadow-2xl p-8 md:p-12 text-center transform hover:scale-105 transition-all duration-500">
            
            {/* Music Control */}
            <div className="absolute top-4 right-4">
              <button
                onClick={toggleMusic}
                className="bg-pink-500 hover:bg-pink-600 text-white rounded-full p-3 shadow-lg transition-all duration-300 hover:scale-110"
                title={isPlaying ? "Pause Music" : "Play Music"}
              >
                {isPlaying ? "🔇" : "🎵"}
              </button>
            </div>

            {/* Main Heading */}
            <div className="mb-8 animate-fadeIn">
              <h1 className="text-4xl md:text-6xl font-bold bg-gradient-to-r from-pink-500 via-red-500 to-pink-600 bg-clip-text text-transparent mb-4">
                Happy Birthday Sameer
              </h1>
              <h2 className="text-2xl md:text-4xl font-semibold text-pink-600 mb-2">
                My Love ❤️
              </h2>
              <p className="text-lg md:text-xl text-gray-600">
                from <span className="font-semibold text-pink-500">Matina – Your Darling</span>
              </p>
            </div>

            {/* Blessings Section */}
            <div className="mb-10 animate-fadeIn animation-delay-500">
              <div className="bg-gradient-to-r from-pink-50 to-red-50 rounded-2xl p-6 border border-pink-200">
                <p className="text-lg md:text-xl text-gray-700 leading-relaxed">
                  "May your day be filled with endless joy, love, and laughter. 
                  You are my heart, my life, my everything. Every moment with you 
                  is a blessing, and I'm grateful for every smile, every laugh, 
                  and every precious memory we share. Happy Birthday, my love ❤️"
                </p>
              </div>
            </div>

            {/* Interactive Buttons */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8 animate-fadeIn animation-delay-1000">
              <button
                onClick={handleHug}
                className="bg-gradient-to-r from-pink-400 to-pink-500 hover:from-pink-500 hover:to-pink-600 text-white font-semibold py-4 px-6 rounded-2xl shadow-lg transform hover:scale-105 transition-all duration-300 hover:shadow-xl"
              >
                Send a Hug 🤗
              </button>
              
              <button
                onClick={handleKiss}
                className="bg-gradient-to-r from-red-400 to-red-500 hover:from-red-500 hover:to-red-600 text-white font-semibold py-4 px-6 rounded-2xl shadow-lg transform hover:scale-105 transition-all duration-300 hover:shadow-xl"
              >
                Send a Kiss 💋
              </button>
              
              <button
                onClick={handleLove}
                className="bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 text-white font-semibold py-4 px-6 rounded-2xl shadow-lg transform hover:scale-105 transition-all duration-300 hover:shadow-xl"
              >
                Shower with Love ❤️
              </button>
            </div>

            {/* Special Message */}
            <div className="animate-fadeIn animation-delay-1500">
              <div className="bg-gradient-to-r from-pink-100 to-red-100 rounded-2xl p-6 border-2 border-pink-300">
                <p className="text-xl md:text-2xl font-bold text-pink-700 mb-2">
                  🎂 Happy Birthday, My Dearest Sameer! 🎂
                </p>
                <p className="text-lg text-gray-700">
                  May this year bring you all the happiness, success, and love you deserve. 
                  You make my world complete! 🌟
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Animated Effects */}
      
      {/* Hug Hearts Effect */}
      {showHearts && (
        <div className="fixed inset-0 pointer-events-none z-50">
          {[...Array(15)].map((_, i) => (
            <div
              key={i}
              className="absolute text-pink-500 text-2xl animate-heartFloat"
              style={{
                left: `${50 + (Math.random() - 0.5) * 40}%`,
                top: `${50 + (Math.random() - 0.5) * 40}%`,
                animationDelay: `${i * 0.1}s`,
              }}
            >
              ❤️
            </div>
          ))}
        </div>
      )}

      {/* Kiss Effect */}
      {showKiss && (
        <div className="fixed inset-0 pointer-events-none z-50">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute text-red-500 text-3xl animate-kissFloat"
              style={{
                left: `${50 + (Math.random() - 0.5) * 60}%`,
                top: `${50 + (Math.random() - 0.5) * 60}%`,
                animationDelay: `${i * 0.05}s`,
              }}
            >
              💋
            </div>
          ))}
        </div>
      )}

      {/* Love Confetti Effect */}
      {showConfetti && (
        <div className="fixed inset-0 pointer-events-none z-50">
          {[...Array(50)].map((_, i) => (
            <div
              key={i}
              className="absolute w-2 h-2 animate-confetti"
              style={{
                left: `${Math.random() * 100}%`,
                top: `-10px`,
                backgroundColor: ['#ff69b4', '#ff1493', '#ff69b4', '#ff1493', '#ff69b4'][i % 5],
                animationDelay: `${i * 0.1}s`,
                animationDuration: `${2 + Math.random() * 2}s`,
              }}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default RomanticBirthdayPage;
